// 密码加密
func ScryptPassword(password string) string {
    const KeyLen = 10 //长度
    salt := make([]byte, 8)

    salt = []byte{12, 22, 111, 46, 82, 3, 7, 21}
    HashPw, err := scrypt.Key([]byte(password), salt, 16384, 8, 1, KeyLen)
    if err != nil {
        log.Fatal(err)
    }
    finalPw := base64.StdEncoding.EncodeToString(HashPw)
    return finalPw
}

